import React, { Component } from 'react';
import ResponsiveMenu from 'react-responsive-navbar';
import firebase from '../config/firebaseconfig';
import { Button } from 'react-bootstrap';
import ditto from './ditto.png';
//Redux
import { connect } from 'react-redux';
import { signOut } from '../redux/actions';
import { Nav } from 'react-bootstrap';
import { withRouter } from 'react-router-dom';

/*
const LoggedInNavbar = (props) => 
{
    return (
        <div>
            <img src={firebase.storage().ref(`images/`+props.auth.uid).getDownloadURL()}></img>
        </div>
    );
}
*/
class LoggedInNavbar extends Component {

    state = {
        image: ""
    }

    componentDidMount() {
        const {profile} = this.props;
        this.setState({
            image: profile.image
        })
    }

    handleClick = (e) => {
        e.preventDefault();
        this.props.signOut(this.props.history);
    }


    render() {
        const { auth, profile } = this.props;
        return (
            <div className="form-inline">
                <Nav.Link href={profile.profile_url}>My Profile</Nav.Link>
                <Nav.Link onClick={this.handleClick}>Sign out</Nav.Link>
                <img src={profile.image ? profile.image : ditto} style={{ height: "50px", width: "50px"}}></img>
            </div>
        );
    }
}


const mapStateToProps = (state) => {
    return {
        auth: state.firebase.auth,
        profile: state.firebase.profile
    }
}

export default connect(
    mapStateToProps, { signOut }
)(withRouter(LoggedInNavbar));
