import React, { Component } from 'react';
import './component.css';
import firebase from '../config/firebaseconfig';
import { Redirect, withRouter } from 'react-router-dom';
import { Form, Button, Alert } from 'react-bootstrap'
//Redux
import { connect } from 'react-redux';
import { signUp } from '../redux/actions';


class SignUp extends Component{

    state = {
        firstname: "",
        lastname: "",
        email: "",
        password: "",
        profileurl: "",
    };

    handleChange = (e) => {
        e.preventDefault();
        this.setState({
            firstname: document.getElementById("fn").value,
            lastname: document.getElementById("ln").value,
            email: document.getElementById("em").value,
            password: document.getElementById("pw").value,
            profileurl: `/profile/${document.getElementById("fn").value}_${document.getElementById("ln").value}_` + Math.random()*1000000
        })
    }

    handleFirstNameChange = (e) => {
        e.preventDefault();
        this.setState({
            firstname: e.target.value
        })
    }

    handleLastNameChange = (e) => {
        e.preventDefault();
        this.setState({
            lastname: e.target.value
        })
    }

    handleEmailChange = (e) => {
        e.preventDefault();
        this.setState({
            email: e.target.value
        })
    }

    handlePasswordChange = (e) => {
        e.preventDefault();
        this.setState({
            password: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        /*
        this.setState({
            first_name: document.getElementById("fn").value,
            last_name: document.getElementById("ln").value,
            email: document.getElementById("em").value,
            password: document.getElementById("pw").value,
            profile_url: `http://localhost:3000/profile/${document.getElementById("fn").value}${document.getElementById("ln").value}`
        })
        */
        this.props.signUp(this.state, this.props.history);
    }

    /*
   handleSubmit = (e) => {
    e.preventDefault();
    firebase.auth().createUserWithEmailAndPassword(document.getElementById("em").value, document.getElementById("pw").value)

    //We should keep the "then" below in order to have a callback function that checks whether the profile url alreay exists in our database.
    //If it does and the email has not been taken, it probably means the two users have the same name. So we can add a random
    //number after the current profile url to distinguish it from the existing one. 

    //.then(() => this.setState({profileurl: `https:localhost:3000/profile/${document.getElementById("fn").value}${document.getElementById("ln").value}`}))
    .then(() => firebase.firestore().collection("users").doc(firebase.auth().currentUser.uid).set({
        first_name: document.getElementById("fn").value,
        last_name: document.getElementById("ln").value,

        //Still need to add validators to check (1) whether the email or the password has been taken and (2) whether the email is valid.
        email: document.getElementById("em").value,
        password: document.getElementById("pw").value,

        //Profile url is basically just the domain name/profile/the cancatenation of the user's first name and last name.
        //I set the value here no in the "then" above to save time.
        profile_url: `http://localhost:3000/profile/${document.getElementById("fn").value}${document.getElementById("ln").value}`
    })).then(() => this.props.history.push(`/profile/${document.getElementById("fn").value}${document.getElementById("ln").value}`));
}
