   render(){


    const signUpStyle = {
        listStyleType: 'none',
        width: "600px",
        //height: "0.05em",
        padding: '100px',
        marginLeft: "540px",
        marginRight: "500px",
        textAlign: "center",
        fontWeight: "bold",
        borderRadius: "3px",
        outline: 'none',
        border: 'solid',
        borderWidth: '10px',
        cursor: 'pointer',
        background: '#fceed1',
        fontFamily: 'FiraSans',
        fontSize: '30px',
        color: '#000000'
        //form only:

    }
    const buttonStyle = {
        height: "50px",
        width: "100px",
        margin: "45px",
        backgroundColor: '#fd9535',
        textAlign: 'center',
        color: "#fff",
        fontSize: "32px",
        shape: "circle",
        fontWeight: "bold",
    }

    const spanStyle = {
        display: "inline-block"
    }

    const emailStyle = {
        marginLeft: "40px",
        marginRight: "5px"
    }

    const passwordStyle = {
        marginLeft: "10px"
    }


    const { authError } = this.props;

    return(
        /*
            <div>
                <Form>
                    <Form.Group>
                      <Form.Label>First name</Form.Label>
                      <Form.Control onChange={this.handleFormChange}></Form.Control>
                    </Form.Group>

                    <Form.Group>
                      <Form.Label>Last name</Form.Label>
                      <Form.Control onChange={this.handleFormChange}></Form.Control>
                    </Form.Group>

                    <Form.Group>
                      <Form.Label>Email address</Form.Label>
                      <Form.Control onChange={this.handleFormChange}></Form.Control>
                    </Form.Group>  

                    <Form.Group>
                      <Form.Label>Password</Form.Label>
                      <Form.Control onChange={this.handleFormChange}></Form.Control>
                    </Form.Group>
                    <div>
                        hello
                        {authError}
                        { authError ? <p>{ authError }</p> : null}
                    </div>
                    <Button type="submit">Register</Button>               
                </Form>
            </div>
