            <form onSubmit={this.handleSubmit} style={{ marginBottom: "20px"}}>

                <div className="form-inline">
                    <span>First name:</span>
                    <input style={{ width: "120px", height: "30px" }} id="fn"  onChange={this.handleChange} required></input>

                    <span>Last name:</span>
                    <input style={{ width: "120px", height: "30px" }} id="ln" onChange={this.handleChange} required></input>

                </div>

                <div className="form-inline">
                    <span>Email:</span>
                    <input style={{ width: "120px", height: "30px", marginLeft: "45px" }} id="em" onChange={this.handleChange} required></input>

                    <span>Password:</span>
                    <input style={{ width: "120px", height: "30px", marginLeft: "10px" }} id="pw" onChange={this.handleChange} required></input>

                </div>

                <Button variant="success" type="submit" style={{ width: "120px" }} color="green">Sign up</Button>

                    { authError ? <div className="auth_alert">{ authError }</div> : null}
            </form>
    )
}
}


