import React, { Component } from 'react';
import { Redirect, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { Button, Card, CardDeck, Image, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import "./components.css";

import {Helmet} from "react-helmet";

//videos
import acmilan_atlanta from "../videos/acmilan_atlanta.mp4";
import paris_stetienne from "../videos/paris_stetienne.mp4";
import udinese_juventus from "../videos/udinese_juventus.mp4";
import liverpool_chelsea from "../videos/liverpool_chelsea.mp4";

//images
import acmilan_vs_atlanta from "../images/acmilan_vs_atlanta.jpeg";
import paris_vs_stetienne from "../images/paris_vs_stetienne.jpg";
import udinese_vs_juventus from "../images/udinese_vs_juventus.jpeg";
import liverpool_vs_chelsea from "../images/liverpool_vs_chelsea.jpeg";

//icons
import liverpool from "../icons/liverpool.jpg";
import acmilan from "../icons/acmilan.png";
import atlanta from "../icons/atlanta.png";
import chelsea from "../icons/chelsea.png";
import juventus from "../icons/juventus.png";
import paris from "../icons/paris.jpeg";
import stetienne from "../icons/stetienne.svg";
import udinese from "../icons/udinese.png";


const highlights = [
  {video: acmilan_atlanta, image: acmilan_vs_atlanta, home: acmilan, away: atlanta, home_name: "AC米兰", away_name: "亚特兰大", tournament: "2019/2020 意甲", round: "第36轮", date: "2020-07-24"},
  {video: paris_stetienne, image: paris_vs_stetienne, home: paris, away: stetienne, home_name: "巴黎圣日尔曼", away_name: "圣埃蒂安", tournament: "2019/2020 法国杯", round: "决赛", date: "2020-07-24"},
  {video: udinese_juventus, image: udinese_vs_juventus, home: udinese, away: juventus, home_name: "乌迪内斯", away_name: "尤文图斯", tournament: "2019/2020 意甲", round: "第35轮", date: "2020-07-23"},
  {video: liverpool_chelsea, image: liverpool_vs_chelsea, home: liverpool, away: chelsea, home_name: "利物浦", away_name: "切尔西", tournament: "2019/2020 英超", round: "第37轮", date: "2020-07-22"}
]

class Home extends Component {

  constructor(props) {
    super(props);

  }

  openHighlight = (video) => {
    this.props.history.push({
      pathname: `/highlight/${Math.random()}`,
      state: { video: video }
    });
  }


  render() {
    return (
      <div style={{ alignItems: "center", position: "relative" }}>

          <div className="header">足球集锦之家⚽</div>

          <div style={{ position: "absolute", marginTop: "9%" }}>最新比赛集锦</div>
          <Container className="highlights-container">
            {highlights && highlights.map((highlight) => {
              return <div onClick={() => this.openHighlight(highlight.video)} style={{marginLeft: "100px", width: "400px"}}>

                <img style={{width:"400px", height:"250px"}} src={highlight.image}></img>

                <div style={{display:"flex"}}>
                  {highlight.tournament} {highlight.round} {highlight.date}
                </div>

                <div style={{display:"flex"}}>

                  <div style={{display: "flex", float:"left"}}>
                    <img className="icon" src={highlight.home}></img>
                    <div>{highlight.home_name}</div>
                  </div>

                  <div>VS</div>

                  <div style={{display: "flex", float:"right"}}>
                    <div>{highlight.away_name}</div>
                    <img className="icon"src={highlight.away}></img>
                  </div>
                </div>

              </div>
            })}
          </Container>

        <Helmet>
          <meta name="description" content="Have Some Company While You're Becoming Your Better Self"></meta>
          <title>Salon</title>
        </Helmet>

      </div>
    );
  }
}



export default Home;
