    }

    handleClose = () => {
      this.setState({ show: false });
    }

    handleCloseRedirect = () => {
      this.setState({ routeguarding: false });
    }

    handleSubmit = (e) => {
      e.preventDefault();
      this.props.submitTicket(this.state);
      this.setState({
        show: false
