import React, { Component, useState } from 'react';
import { withRouter } from 'react-router-dom';
import firebase from '../config/firebaseconfig';
import '../styles/home.css';
import '../styles/profile.css';
import NavBar from './navbar';
import Settings from './settings';
import './component.css';
import ditto from './ditto.png';

import { Modal, Button, Card, CardDeck, Image, ListGroup, } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

//Redux
import { connect } from 'react-redux';
import { compose } from 'redux';
import { firestoreConnect } from 'react-redux-firebase';
import { uploadImage } from '../redux/actions.js';

//The static profile page that displays a user's profile. Check whether the profile url is the same as the profile_url of the current logged-in user
//and do a conditional rendering of an "Edit" button that redirects users to the settings page. [TO BE COMPLETED].

class Profile extends Component {
