    constructor(props){
      super(props);
      this.state = {
        edit: false
       }
    }




    /*
    componentDidMount() {
      const { profile } = this.props;
      this.setState({
        image: profile.image
      })
    }
    */

    handleUpload = (e) => {
      e.preventDefault();
      const image = e.target.files[0];
      const { auth } = this.props;
      this.props.uploadImage(image, auth.uid);
  }

  openFiles = (e) => {
      e.preventDefault();
      const input = document.getElementById("imageinput");
      input.click();
  }

    handleShow = (e) => {
      e.preventDefault();
      this.setState({ edit : true });
    }

    handleClose = () => {
      this.setState({ edit : false });
    }

    render() {
      /*
        const items=this.state.areasofinterest;
        const name=this.state.firstname + " " + this.state.lastname;
        const email=this.state.email;
      */

     const { users, auth, profile } = this.props;
     const name = profile.first_name + " " + profile.last_name;
     console.log(auth.uid);
     console.log(profile.email);

      return (
        <div>
      { auth.uid && profile.first_name ? 
        <div>
        {<NavBar />}
          <div className="container">
            <link
              href='https://fonts.googleapis.com/css2?family=Roboto+Slab:wght@300;400;500&display=swap'
              rel='stylesheet'
            />
            <div className='profile'>
              <CardDeck  >
                <Card style={{border: "0"}}>
                  <Card.Body className='profile-card'>

                  <img src={profile.image ? profile.image : ditto}></img>
                  <input type="file" id="imageinput" hidden="hidden" onChange={this.handleUpload}></input>
                  <Button variant="success" onClick={this.openFiles}>Upload Profile Image</Button>

                    <Card.Title className="text-center">{name}</Card.Title>


                    <Card.Text>
                    <div className="basic-info">
                        <li>‚òØÔ∏è Gender: {profile.gender}</li><br/>
                    </div>
                    </Card.Text>

                    <Card.Text>
                    <div className="basic-info">
                        <li>üíª Occupation: {profile.role} {profile.role !== "" && profile.institution !== "" ? "at" : null} {profile.institution}</li><br/>
                    </div>
                    </Card.Text>

                    <Card.Text>
                    <div className="basic-info">
                        <li>üí© Self description: {profile.self_description}</li><br/>
                    </div>
                    </Card.Text>

                    <Card.Text>

                      <div className="basic-info">
                      üëª Values: 
                    {profile.personalities && profile.personalities.map((personality) => {
                      return (
                        <div className="unselected-button">
                          {personality}
                        </div>
                      );
                    })}
                    </div>
                    </Card.Text>

                    <Card.Text>

                      <div className="basic-info">
                      üíõ Interests: 
                    {profile.areas_of_interest && profile.areas_of_interest.map((interest) => {
                      return (
                        <div className="unselected-button">
                          {interest}
                        </div>
                      );
                    })}
                    </div>
                    </Card.Text>

                    <Card.Text>
                    <div className="basic-info">
                        <li>üíå Email: {profile.email}</li><br/>
                    </div>
                    </Card.Text>


                    { auth.uid ? <div><Button variant="success" onClick={this.handleShow}>Edit</Button>
                                  <Modal show={this.state.edit} onHide={this.handleClose}>
                                  <Modal.Header closeButton></Modal.Header>
                                  <Modal.Body><Settings handleClose={this.handleClose}/></Modal.Body>
                                  </Modal></div> : null}

                  </Card.Body>
                </Card>

              </CardDeck>
            </div>
        </div>
        </div>
        : <div class="loader"></div>}
        </div>
        );
    }
}

//{ auth.uid ? <Button variant='success' onClick={this.handleClick}>Edit</Button> : null}

const mapStateToProps = (state) => {
  return {
      auth: state.firebase.auth,
      profile: state.firebase.profile,
      users: state.firestore.ordered.users
  }
}

export default compose(
  connect(mapStateToProps, { uploadImage }),
  firestoreConnect([
    { collection: 'users' }
  ])
)(withRouter(Profile));
