import { UPDATE_SUCCESS, MATCHING_UPDATE, MATCHING_CHAT, AUTH_SUCCESS, AUTH_FAIL, EDIT_SUCCESS, SUBMIT_SUCCESS, SIGN_OUT, MATCHING_REQUEST, MATCHING_SUCCESS, MATCHING_FAIL, CHAT_SUCCESS, BOTTLE_SUCCESS, BOTTLE_FAIL } from "./types.js";

const initialState = {
    authError: null,
    loading: false,
    chatid: "",
    buddy: "",
    sent: 0,
    showmatchingwindow: false,
    matching: localStorage.getItem("matching"),
    previousstate: {numberofparticipants: 0, numberofnotifications: 0}
}

const authReducer = (state = initialState, action) => {
    const {type, payload} = action;

    switch(type) {
        case AUTH_SUCCESS:
            return {
                ...state,
                authError: null
            }
        case AUTH_FAIL:
            return {
                ...state,
                authError: action.err.message
            }
        case EDIT_SUCCESS:
            return {
                ...state,
                authError: null
            }
        case SUBMIT_SUCCESS:
            return {
                ...state,
                authError: null
            }
        case SIGN_OUT:
            return {
                state
            }
        case CHAT_SUCCESS:
            return {
                ...state,
            }
        case MATCHING_CHAT:
            return {
                ...state,
                chatid: action.payload
            }
        case MATCHING_REQUEST:
            return {
                ...state,
                loading: true,
                buddy: "",
                chatid: ""
            }
        case MATCHING_UPDATE:
            return {
                ...state,
                matching: action.payload
            }
        case MATCHING_SUCCESS:
            return {
                ...state,
                buddy: action.payload,
                loading: false,
                matching: {going: false, pool: "", map: ""}
            }
        case MATCHING_FAIL:
            return {
                ...state,
                loading: false,
                matching: {going: false, pool: "", map: ""}
            }
        case BOTTLE_SUCCESS:
            return {
                ...state,
                sent: 1,
                loading: false
            }
        case BOTTLE_FAIL:
            return {
                ...state,
                sent: 2,
                loading: false
            }
        case BOTTLE_FAIL:
            return {
                ...state,
                previousstate: payload
            }
        default: return state;
    }
}

export default authReducer;
