import React, { Component } from 'react';
import "./component.css";
import { connect } from 'react-redux';
import firebase from '../config/firebaseconfig';
import { compose } from 'redux';
import { firestoreConnect } from 'react-redux-firebase';
import { startConversation } from '../redux/actions';
import { Rnd } from "react-rnd";


class ChatWindow extends Component {

    constructor(props){
        super(props);
        this.state = {
            messages: [],
            width: 300,
            height: 500,
            x: -350,
            y: 0
        }
    }

    componentDidMount() {
        this.newData.scrollTop = this.newData.scrollHeight;
        firebase.firestore().collection("chats").doc(this.props.chat.chat_id).get()
        .then((doc) => {
            this.setState({
                messages: doc.data().messages
            })
        })
    }

    componentDidUpdate(prevprops) {
        this.newData.scrollTop = this.newData.scrollHeight;
        if (prevprops.chat !== this.props.chat) {
            firebase.firestore().collection("chats").doc(this.props.chat.chat_id).get()
            .then((doc) => {
                this.setState({
                    messages: doc.data().messages
                })
            })
        }
    }


    componentWillReceiveProps() {
        console.log("called");
        this.newData.scrollTop = this.newData.scrollHeight;
        firebase.firestore().collection("chats").doc(this.props.chat.chat_id).get()
        .then((doc) => {
            this.setState({
                messages: doc.data().messages
            })
        })
    }





    handleSend = async (e) => {
        e.preventDefault();
        if (document.getElementById("message").value !== "") {
        const date = new Date();
        const time = date.getFullYear() + "-" + (date.getMonth() + 1) + "-" + date.getDate() + " " + date.getHours() + ":" + date.getMinutes();
        const message = this.createMessage(time);
        const { profile } = this.props;
        firebase.firestore().collection("users").doc(this.props.chat.id).get()
        .then((doc) => {
            this.props.startConversation(this.props.chat.id, doc.data(), profile, message);
        })
        .then(() => document.getElementById("message").value = "");
    }
      }

      createMessage = (time) => {
        let message = {};
        message = { time: time, from : this.props.auth.uid, content: document.getElementById("message").value };
        return message;
      }


    render() {

        const chats = this.state.messages;
        const { auth, profile } = this.props;

        return (


<Rnd
className="chats-window"
size={{ width: this.state.width, height: this.state.height }}
enableUserSelectHack="false"
position={{ x: this.state.x, y: this.state.y, z: 1 }}
onDragStop={(e, d) => {
  this.setState({ x: d.x, y: d.y });
}}
onResizeStop={(e, direction, ref, delta, position) => {
  this.setState({
    width: ref.style.width,
    height: ref.style.height,
    ...position
  });
}}
>

                <div className="chats-header">
                        <div className="vertical-center">
                            <img src={this.props.chat.friend_image} style={{ height: "30px", width: "30px" }}></img>
                            <span>{this.props.chat.friend}</span>
                        </div>
                    <button className="chatwindow-button" onClick={() => this.props.handleClose()} style={{ float: "right" }}>x</button>
                </div>

                <div id="chats" className="chats" ref={(ref) => this.newData = ref}>
                    {chats && chats.map((chat) => {
                        return (
                            <div className={ chat.from === auth.uid ? "me-section" : "buddy-section" }>
                            <div style={ chat.from === auth.uid ? { display: "flex", float: "right", clear: "both" } : { display: "flex", float : "left", clear: "both" }}>
                                <img src={ chat.from === auth.uid ? profile.image : this.props.chat.friend_image} style={{ height: "20px", width: "20px" }}></img>
                                {chat.time}
                            </div>

                            <div className={ chat.from === auth.uid ? "right-bubble" : "left-bubble" }>
                            {chat.content}
                            </div>
                            </div>
                        );
                    })}
                </div>


                <textarea className="chats-textarea" id="message" placeholder="Type your message..." onMouseDown={e => e.stopPropagation()} required />

                <div className="chats-bottom">
                    <button type="submit" className="chatwindow-button" onClick={this.handleSend}>Send</button>
                </div>

            </Rnd>


        );
    }
}

const mapStateToProps = (state) => {
    return {
        chats: state.firestore.ordered.chats,
        profile: state.firebase.profile,
        auth: state.firebase.auth,
        chatid: state.auth.chatid
    }
}


export default compose(
connect(mapStateToProps, {startConversation}),
firestoreConnect([
    { collection: 'chats' }
    ])
)(ChatWindow);
