    "psychology",
    "economics",
    "history",
    "politics",
    "law",
    "healthcare",
    "arts"
]

class Settings extends Component {  


        state = {
            firstname: "",
            lastname: "",
            gender: "",
            institution: "",
            role: "",
            selfdescription: [],
            areasofinterest: "",
            email: "",
            profileimage: "",
            personalities: [],
        };     




        componentDidMount() {

           const { profile, auth } = this.props;
           const id = auth.uid;
           const image = firebase.storage().ref(`images/`+id);
           image.getDownloadURL().then((url) => this.setState({ profileimage: url }));
           this.setState({
                firstname: profile.first_name,
                lastname: profile.last_name,
                gender: profile.gender,
                institution: profile.institution,
                role: profile.role,
                selfdescription: profile.self_description,
                areasofinterest: profile.areas_of_interest,
                email: profile.email,
                personalities: profile.personalities,
           }
           )
        }

        componentWillReceiveProps(nextprops) {
            const { profile, auth } = nextprops;
            const id = auth.uid;
            const image = firebase.storage().ref(`images/`+id);
            image.getDownloadURL().then((url) => this.setState({ profileimage: url }));
            this.setState({
                 firstname: profile.first_name,
                 lastname: profile.last_name,
                 gender: profile.gender,
                 institution: profile.institution,
                 role: profile.role,
                 selfdescription: profile.self_description,
                 areasofinterest: profile.areas_of_interest,
                 email: profile.email,
                 personalities: profile.personalities
            }
            )

        }


        handleSubmit = (e) => {
            e.preventDefault();
            const { auth } = this.props;
            this.props.editProfile(this.state, auth.uid, this.props.history);
            this.props.handleClose();
        }


        handleChange = (e) => {
            this.setState({
                [e.target.id]: e.target.value
            })
        }

        handleTags = (e) => {
            e.preventDefault();
            if (e.target.className === "unselected-button") {
                const helper = this.state.areasofinterest;
                helper.push(e.target.value);
                this.setState({ areasofinterest: helper });
                e.target.className = "selected-button";
            } else
            {
                const newareasofinterest = this.state.areasofinterest.filter(tag => tag !== e.target.value);
                this.setState({ areasofinterest: newareasofinterest });
                e.target.className = "unselected-button";
            }
        }

        handlePersonalities = (e) => {
            e.preventDefault();
            if (e.target.className === "unselected-button") {
                const helper = this.state.personalities;
                if (helper.length < 7) {
                    helper.push(e.target.value);
                    this.setState({ personalities: helper });
                    e.target.className = "selected-button";
                }
            } else
            {
                const newpersonalities = this.state.personalities.filter(tag => tag !== e.target.value);
                this.setState({ personalities : newpersonalities });
                e.target.className = "unselected-button";
            }
        }

        renderPersonalities = (personality) => {
            return (
                <button className={this.state.personalities && this.state.personalities.includes(personality) ? "selected-button" : "unselected-button"} value={personality} onClick={this.handlePersonalities}>{personality}</button>
            );
        }

        renderInterests = (interest) => {
            return (
                <button className={this.state.areasofinterest && this.state.areasofinterest.includes(interest) ? "selected-button" : "unselected-button"} value={interest} onClick={this.handleTags}>{interest}</button>
            );
        }



       render(){



        const { profile } = this.props;

        return(

            <form>

                        <span>First Name 💘</span>
                        <input id="firstname" value={this.state.firstname} onChange={this.handleChange}></input>

                        <span><br/>Last Name 💝</span>
                        <input id="lastname" value={this.state.lastname} onChange={this.handleChange}></input>


                        <div style={{ marginTop: "15px" }}>
                            <span>Gender 😽</span>
                        </div>

                        <div className="form-inline">

                            <label>Male</label><input type="radio" id="gender" value="Male" onChange={this.handleChange} checked={this.state.gender === "Male"} />



                            <label>Female</label><input type="radio" id="gender" value="Female" onChange={this.handleChange} checked={this.state.gender === "Female"} />




                            <label>Other</label><input type="radio" id="gender" value="Other" onChange={this.handleChange} checked={this.state.gender === "Other"} />

                        </div>





                        <span><br/>Institution 🏢</span>
                        <input id="institution" value={this.state.institution} onChange={this.handleChange}></input>

                        <span><br/>Title 🎗</span>
                        <input id="role" value={this.state.role} onChange={this.handleChange}></input>

                        <span><br/>Describe yourself! 💩</span>
                        <textarea id="selfdescription" value={this.state.selfdescription} onChange={this.handleChange}></textarea>

                        <div style={{ marginTop: "15px" }}>
                            <span>Select up to 7 values/characteristics that resonate with you the most! 👻</span>
                        </div>


                        { personalities && personalities.map((personality) =>{
                            return this.renderPersonalities(personality);
                        } )}

                        <div style={{ marginTop: "15px" }}>
                            <span>Select your interests! 💛</span>
                        </div>

                        { interests && interests.map((interest) =>{
                            return this.renderInterests(interest);
                        } )}



                        <span><br/>Email 💌</span>
                        <input id="email" value={this.state.email} onChange={this.handleChange}></input>

                        <br/>
                        <Button type="submit" variant="success" onClick={this.handleSubmit} style={{ marginTop: "10px" }}>Save</Button>
            </form>

        )
    }
}


const mapStateToProps = (state) => {
    return {
        profile: state.firebase.profile,
        auth: state.firebase.auth,
    }
}

const mapActionsToProps = { editProfile, uploadImage };

export default connect(mapStateToProps, mapActionsToProps )(withRouter(Settings));



