import React, { useState, Component } from 'react';
import { Link } from 'react-router-dom';
import '../styles/home.css';
import '../styles/streamingplaza.css';
import '../styles/waitingroom.css';

import { Alert, Button, Card, CardDeck, Image, ListGroup, Modal, Form, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import NavBar from './navbar';
import StreamingCard from './design/streamingcard';
import ChatBox from './chatbox.js';
import firebase from '../config/firebaseconfig';

//Redux
import { connect } from 'react-redux';
import { compose } from 'redux';
import { firestoreConnect } from 'react-redux-firebase';
import { startStreaming } from '../redux/actions';

//[TO BE COMPLETED].

/*
<Form.Group controlId="link">
<Form.Control inputRef={(ref) => this.link = ref}></Form.Control>
</Form.Group>

<Form.Group controlId="description">
  <Form.Control inputRef={(ref) => this.description= ref}></Form.Control>
</Form.Group>
