//For the minimum viable product, this page will look very similar to the waiting room page except that (1) users post their meeting links
//on this page and (2) we may need to use Zoom's API to get the number of participants in a meeting and display the number on the page.

const taskfilters = [
  'Startup/Personal Project',
  'Work/Internship',
  'Study',
  'Interview Preparation',
  'Exam Preparation',
  'Reading Books',
  'Research',
  'Brainstorming',
  'Finance/Investment/VC',
  'Business Analysis',
  'Software Engineering',
  'Data Science',
  'UI/UX Design',
  'Artificial Intelligence',
  'Electrical Engineering',
  'Mechanical Engineering',
  'Biomedical Engineering',
  'Mathematics',
  'Physics',
  'Chemistry',
  'Psychology',
  'Economics',
  'History',
  'Politics',
  'Law',
  'Healthcare',
  'Arts'
];

class StreamingPlaza extends Component {

  /*
  constructor(props){
    super(props);
    this.state = {
      link: "",
      description: "",
      show: false,
      routeguarding: false,
      tasks: [],
      reduxtasks: [],
      filterVisibility: false
      }

      this.roomclicks = React.createRef([]);
      this.child = React.createRef();
  }
  */

  state = {
    link: "",
    description: "",
    show: false,
    routeguarding: false,
    tasks: [],
    reduxtasks: [],
    filterVisibility: false
    }

    //roomclicks = React.createRef([]);

  componentDidMount() {
    const { auth } = this.props;
    /*
    setTimeout(() => {
      console.log(this.child);
      console.log(this.child.current);
      this.child.current.handleShow();
    }, 4000);
    */
    if (auth.uid) {
      firebase.firestore().collection("rooms").doc(auth.uid)
      .get()
      .then((doc) => {
        if (doc.exists) {
          this.setState({
            link: doc.data().meeting_link,
            description: doc.data().room_description
          })
        }
      })
    }
  }

  randomClick = (e) => {
    e.preventDefault();
    this.roomclicks.current[0].handleClick();
  }

  handleSubmit = async (e) => {
    e.preventDefault();
    await this.props.startStreaming(this.state);
    this.handleClose();
  }

  handleShow = (e) => {
    e.preventDefault();
    const { auth } = this.props;
    if (!auth.uid) {
      this.setState({ routeguarding: true });
    } else this.setState({ show: true });
  }

  handleClose = () => {
    this.setState({ show: false });
  }

  handleCloseRedirect = () => {
    this.setState({ routeguarding: false });
  }

  handleChange = (e) => {
    e.preventDefault();
    this.setState({
      [e.target.id]: e.target.value
    })
  }

  setRef = (ref) => {
    this.roomclicks.push(ref);
  }


  renderRoom = (room) => {
    if (room.open === true) {
      if (room.id !== "hAOhg4jmoHVsjQ0nRva0l4ElBzk2" && this.state.tasks.every(t => room.tasks_tags && room.tasks_tags.includes(t))) {
        return <StreamingCard info={room}></StreamingCard>;
      }
    }
  }

  updateReduxTasks = (e) => {
    e.preventDefault();

   if (e.target.className === "unselected-button") {
    const helper = this.state.reduxtasks;
    helper.push(e.target.id);
    this.setState({ reduxtasks : helper });
    e.target.className = "selected-button";
   }
    else {
      const newfilters = this.state.reduxtasks.filter(tag => tag !== e.target.id);
      this.setState({ reduxtasks : newfilters });
      e.target.className = "unselected-button";
    }
  }


  updateTasks = (e) => {
    e.preventDefault();
   if (e.target.className === "unselected-button") {
    const helper = this.state.tasks;
    helper.push(e.target.id);
    this.setState({ tasks : helper });
    e.target.className = "selected-button";
   }
    else {
      const newfilters = this.state.tasks.filter(tag => tag !== e.target.id);
      this.setState({ tasks : newfilters });
      e.target.className = "unselected-button";
    }
  }

  showFilter = (e) => {
    e.preventDefault();
    this.setState({
      filterVisibility: true
    })
  }

  closeFilter = () => {
    this.setState({
      filterVisibility: false
    })
  }


  render() {

    const { auth, rooms, profile } = this.props;

    let founderprofile;
    if (rooms) {
      const founder = rooms.find(element => element.id === "hAOhg4jmoHVsjQ0nRva0l4ElBzk2");
      if (founder.open === true) {
        founderprofile = <StreamingCard info={founder}></StreamingCard>;
      }

    }


    return (

      <div>
      {auth.uid || (auth.isEmpty && auth.isLoaded) ? 
      <div>
        {<NavBar />}

        {sessionStorage.noFirstVisit === 1 ? <Alert  style={{ width: "600px", position: "absolute", top: "10%", left: "20%" }} variant="warning">LOL Bear with the simplicity of the website! We will make it more like an actual live streaming site if people find it to be useful.😣</Alert> : null }

        {auth.uid ?
           <div className="chatbox-header">
             <ChatBox roomid={auth.uid} />
           </div>
           : null}


          <Container fluid>

          <div>
        <Button className="update-button" onClick={this.handleShow} style={{ position: "absolute", top: "15%", fontSize: "large", fontFamily: "Comic Sans MS, cursive, sans-serif"}}>Start/update streaming</Button>
        </div>

        <div>
        <Button variant="success" onClick={this.showFilter} style={{ marginLeft: "70px", position: "absolute", top: "25%", fontFamily: "Lucida Console, Monaco, monospace", fontSize: "large" }}>Filter by tags</Button>
        </div>

            <Modal show={this.state.filterVisibility} onHide={this.closeFilter}>
            <div className="form-inline" style={{ margin: "5px "}}id="filters">
              {taskfilters.map(filter => {
                return (
                  <div className={this.state.tasks && this.state.tasks.includes(filter) ? "selected-button" : "unselected-button"} id={filter} onClick={this.updateTasks}>
                    {filter}
                    </div>
                );
              })}
            </div>
            </Modal>


        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton></Modal.Header>
          <Modal.Body>

            <form onSubmit={this.handleSubmit}>
            <div style={{ marginBottom: "20px"}}>
              <span style={{ fontSize: "large", fontFamily: "Trebuchet MS, Helvetica, sans-serif"}}>Meeting link:</span>
              <input id="link" value={this.state.link} onChange={this.handleChange} required></input>
            </div>

            <div style={{ marginBottom: "20px"}}>
              <span style={{ fontSize: "large", fontFamily: "Trebuchet MS, Helvetica, sans-serif"}}>Enter a description:</span>
              <textarea id="description" value={this.state.description} onChange={this.handleChange} required></textarea>
              </div>

              <div style={{ marginBottom: "20px"}}>
              <span style={{ fontSize: "large", fontFamily: "Trebuchet MS, Helvetica, sans-serif"}}>Select a few tags:</span>
                    <div className="form-inline">
                    {taskfilters.map(filter => {
                      return (
                        <div className={this.state.reduxtasks && this.state.reduxtasks.includes(filter) ? "selected-button" : "unselected-button" } id={filter} onClick={this.updateReduxTasks}>
                          {filter}
                        </div>
                      )
                    })}
                    </div>
                    </div>

              <Button variant="success" type="submit">Submit</Button>
            </form>
          </Modal.Body>
        </Modal>

        <Modal show={this.state.routeguarding} onHide={this.handleCloseRedirect}>
          <Modal.Header closeButton></Modal.Header>
          <Modal.Body>
            Sign up to create a profile before streaming so that other users can know more about you!😊
          </Modal.Body>
        </Modal>


        <Container fluid style={{ position: "absolute", top: "30%", zIndex: "-1"}}>
        <div className="form-inline" ref={this.roomclicks}>
        {founderprofile}
        { rooms && rooms.map (room => {
          return this.renderRoom(room);  
        })
      }


        </div>
        </Container>


        </Container>


      </div>
      : <div class="loader"></div>}
      </div>
    );
  }
}
