const mapStateToProps = (state) => {
  return {
    rooms: state.firestore.ordered.rooms,
    auth: state.firebase.auth,
    profile: state.firebase.profile
  }
}

export default compose(
  connect(mapStateToProps, {startStreaming}),
  firestoreConnect([
    { collection: 'rooms' }
  ])
)(StreamingPlaza);



