import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App.js';
import * as serviceWorker from './serviceWorker';
import firebase from './config/firebaseconfig'

//Redux
import rootReducer from './redux/rootReducer';
import 'firebase/firestore' // <- needed if using firestore
import { createStore, combineReducers, applyMiddleware, compose } from 'redux'
import { firebaseReducer, reactReduxFirebase, getFirebase } from 'react-redux-firebase'
import { createFirestoreInstance, firestoreReducer, getFirestore, reduxFirestore } from 'redux-firestore' // <- needed if using firestore
import thunk from 'redux-thunk'
import { Provider } from 'react-redux'



const store = createStore(rootReducer,
    compose(
        applyMiddleware(thunk.withExtraArgument({ getFirebase, getFirestore })),
        reduxFirestore(firebase),
        reactReduxFirebase(firebase, {useFirestoreForProfile: true, userProfile: "users"})
) 
);


    ReactDOM.render(<Provider store={store}><App /></Provider>, document.getElementById('root'));
    serviceWorker.unregister();




