import React, { Component } from 'react';
import './component.css';
import firebase from '../config/firebaseconfig';
import { Container, Navbar } from 'react-bootstrap';
import Card from './design/card';
import Filter from './design/filter';
import '../styles/waitingroom.css';
import { thisTypeAnnotation } from '@babel/types';
import NavBar from './navbar';
import ChatBox from './chatbox.js';
import LoggedOutNavbar from './loggedoutnavbar';

import '../styles/card.css';

import SignUp from './signup';
import LogIn from './login';
import { Button, Modal } from 'react-bootstrap';
import MatchingBox from './matchingbox.js';
import MatchingWindow from './matchingwindow.js';
import {Helmet} from "react-helmet";

//Redux
import { connect } from 'react-redux';
import { compose } from 'redux';
import { firestoreConnect } from 'react-redux-firebase';
import { submitTicket, startMatching, groupMessaging } from '../redux/actions';


const personalities = [
    "hard-working",
    "helpful/kind/altruistic",
    "outgoing/social",
    "knowledgeable",
    "ambitious",
    "thoughtful",
    "observant",
    "introvert",
    "humble",
    "imaginative",
    "impartial",
    "intelligent",
    "creative",
    "ethics",
    "social justice",
    "insightful/perceptive",
    "energetic",
    "smart",
    "curious",
    "open-minded",
    "meticulous",
    "logics",
    "agreeable",
    "competent",
    "wealth",
    "social influence/recognition",
    "geek",
    "executive",
    "leader",
    "adventurer",
    "thinker",
    "analyst",
    "planner",
    "artist",
    "designer"
]

const filters = [
    "entrepreneurship",
    "finance/investing/vc",
    "business analysis/consulting",
    "software engineering",
    "data analysis/science",
    "ui/ux design",
    "artificial intelligence",
    "electrical engineering",
    "mechanical engineering",
    "biomedical engineering",
    "mathematics",
    "physics",
    "chemistry",
    "psychology",
    "economics",
    "history",
    "politics",
    "law",
    "healthcare",
    "arts"
];

const taskfilters = [
  'startup/personal project',
  'work/internship',
  'study',
  'interview preparation',
  'exam preparation',
  'reading books',
  'research',
  'brainstorming',
  'finance/investing/vc',
  'business analysis/consulting',
  "software engineering",
    "data analysis/science",
    "ui/ux design",
    "artificial intelligence",
    "electrical engineering",
    "mechanical engineering",
    "biomedical engineering",
    "mathematics",
    "physics",
    "chemistry",
    "psychology",
    "economics",
    "history",
    "politics",
    "law",
    "healthcare",
    "arts"
];



  class WaitingRoom extends Component {

    state = {
      filters: [],
      tasks: [],
      show: false,
      showmatching: false,
      routeguarding: false,
      description: "",
      reduxtasks: [],
      personalities: [],
      genderpreference: "",
      //showmatchingwindow: false,
      showgroupmessaging: false,
      currentprofilelist: [],
      number: 0,
      showmatchingwindow: false,
      interestFilterVisibility: false,
      taskFilterVisibility: false,
      personalityFilterVisibility: false,
      changeMatchingButton: false,
      matching: {}
    }

    buildList = async (users) => {
      var profilelist = [];
      var i = 0;
      while (users && i < users.length) {
