          <div style={{ position: "absolute", marginTop: "9%" }}>最新比赛集锦</div>
          <Container className="highlights-container">
            {highlights && highlights.map((highlight) => {
              return <div onClick={() => this.openHighlight(highlight.video)} style={{marginLeft: "100px", width: "400px", display:"inline-block"}}>

                <img style={{width:"400px", height:"250px"}} src={highlight.image}></img>

                <div style={{display:"flex"}}>
                  {highlight.tournament} {highlight.round} {highlight.date}
                </div>

                <div style={{display:"flex"}}>

                  <div style={{display: "flex", float:"left"}}>
                    <img className="icon" src={highlight.home}></img>
                    <div>{highlight.home_name}</div>
                  </div>

                  <div>VS</div>

                  <div style={{display: "flex", float:"right"}}>
                    <div>{highlight.away_name}</div>
                    <img className="icon"src={highlight.away}></img>
                  </div>
                </div>

              </div>
            })}
          </Container>

        <Helmet>
          <meta name="description" content="Have Some Company While You're Becoming Your Better Self"></meta>
          <title>Salon</title>
        </Helmet>

      </div>
    );
  }
  */
