import React, { Component } from 'react';
import { connect } from 'react-redux';
import MatchingWindow from './matchingwindow.js';
import { Modal } from 'react-bootstrap';
import './component.css';


class MatchingBox extends Component {


    render() {

        let box;
        if (this.props.loading === true) {
            box = <div className="loader"></div>
        }
        else {
            if (this.props.buddy !== "") {

                box = <div>

                <div className="form-inline">

                    <img src={this.props.buddy.image}></img>
                    <img src={this.props.profile.image}></img>
                </div>
                <MatchingWindow chat={this.props.chatid} buddy={this.props.buddy} />

                </div>

            }
            else {
                box = <span>Sorry we cannot help you find a study/work buddy currently ðŸ˜–</span>
            }
        }
        return (

                {box}


        );
    }

}

const mapStateToProps = (state) => {
    return {
        auth: state.firebase.auth,
        loading: state.auth.loading,
        chatid: state.auth.chatid,
        buddy: state.auth.buddy,
        profile: state.firebase.profile
    };
}

export default connect(mapStateToProps)(MatchingBox);
