                            return this.renderPersonalities(personality);
                        } )}

                        <div style={{ marginTop: "15px" }}>
                            <span>Select your interests! üíõ</span>
                        </div>

                        { interests && interests.map((interest) =>{
                            return this.renderInterests(interest);
                        } )}



                        <span><br/>Email üíå</span>
                        <input id="email" value={this.state.email} onChange={this.handleChange}></input>

                        <br/>
                        <Button type="submit" variant="success" onClick={this.handleSubmit} style={{ marginTop: "10px" }}>Save</Button>
            </form>

        )
    }
}


