class StreamingCard extends Component {

  constructor(props){
    super(props);
    this.state = {
      user: "",
      show: false,
      showProfile: false,
      showParticipants: false,
      whoseProfile: false
    }
  }

/*
  state = {
    user: "",
    show: false,
    showProfile: false,
    showParticipants: false,
    whoseProfile: false
  }
  */

  componentDidMount() {
    firebase.firestore().collection("users").doc(this.props.info.host)
    .get()
    .then((doc) => {
      this.setState({
        user: doc.data()
      })
    })
  }

  handleShow = (e) => {
    e.preventDefault();
    this.setState({
      show: true
    })
  }

  handleClose = () => {
    this.setState({
      show: false
    })
  }

  handleRemove = (e) => {
    e.preventDefault();
    this.props.removeStreaming();
  }

  handleJoin = async (e) => {
    e.preventDefault();

      const date = new Date();
      const time = date.getFullYear() + "-" + (date.getMonth() + 1) + "-" + date.getDate() + " " + date.getHours() + ":" + date.getMinutes();
      this.props.joinSession(this.props.info.host, time);

    window.open(this.props.info.meeting_link);
  }

  handleClick = () => {
    console.log("called");
    //document.getElementById("link").click();
  }

  renderParticipants = (e) => {
    e.preventDefault();
    this.setState({
      showParticipants: true
    })
  }

  hideParticipants = () => {
    this.setState({
      showParticipants: false
    })
  }


  renderProfile = (id) => {
      this.setState({
          showProfile: true,
          whoseProfile: id
      })
  }

  hideProfile = () => {
      this.setState({
          showProfile: false
      })
  }

  render() {

    const user = this.state.user;
    const {auth} = this.props;

    return (
      <div className="card-container">
        <div className='profile-content'>



            <div>
              <img src={user.image ? user.image : ditto}></img>
              <Button variant="success" onClick={this.handleShow}>üëÄ More about me</Button> {auth.uid === this.props.info.host ? <Button variant="danger" onClick={this.handleRemove}>Remove</Button> : null}
              <Modal show={this.state.show} onHide={this.handleClose}>
                  <HostCard info={user}></HostCard>
                  </Modal>

            </div>

            <div className="form-inline">
            <div class="info-name">
            üòé Host: {user.first_name} {user.last_name}
            </div>

            {user.gender === "Male" ? <div style={{ backgroundColor: "skyblue", color: "white", width: "20px", textAlign: "center", borderRadius: "10%", marginLeft: "5px" }}>‚ôÇ</div> : null}
          {user.gender === "Female" ? <div style={{ backgroundColor: "pink", color: "white", width: "20px", textAlign: "center", borderRadius: "10%", marginLeft: "5px" }}>‚ôÄ</div> : null}
          {user.gender === "Other" ? <div style={{ backgroundImage: "linear-gradient(to right, skyblue, pink)", color: "white", width: "20px", textAlign: "center", borderRadius: "10%", marginLeft: "5px" }}>‚ö•</div> : null}

          <div className="info" style={{ marginLeft: "30px" }}>
          üíª Occupation: <li style={{ marginLeft: "5px" }}>{user.role} {user.institution && user.role ? "at" : null} {user.institution}</li>
          </div>
          </div>


          <div className="info">
          ‚úîÔ∏è Streaming Link: <a id="link" style={{ marginLeft: "5px" }} target="_blank" rel="noopener noreferrer" href={this.props.info.meeting_link} onClick={this.handleJoin}>{this.props.info.meeting_link}</a>
          </div>
          <div className="info">
          üé§ Room description: <li style={{ marginLeft: "5px" }}>{this.props.info.room_description}</li>
          </div>

          <div className="info">
        <div className="form-inline">
          {this.props.info.tasks_tags && this.props.info.tasks_tags.map((p) => {
            return(
              <div className="unselected-button" style={{ fontSize: "small" }}>
                {p}
              </div>
            );
          })}
        </div>
        </div>

        <div>
        <Button variant="success" onClick={this.renderParticipants}>{this.props.info.participants.length} people have joined the room</Button>
        </div>

        <Modal show={this.state.showParticipants} onHide={this.hideParticipants}>
          <Modal.Header closeButton>
          {this.state.showProfile ? <div className="arrow"><ProfileCard id={this.state.whoseProfile}></ProfileCard></div> : null}
            <div style={{overflowY: "scroll"}}>
              {this.props.info.participants && this.props.info.participants.map((participant) => {
                return (
                  <button className="chatbox-section">
                       <div className="form-inline">
                          <img src={participant.image !== "" ? participant.image : ditto}  onMouseOver={() => this.renderProfile(participant.id)} onMouseOut={this.hideProfile} style={{ width: "40px", height: "40px" }}></img>
                          <span style={{ color: "black", fontSize: "small" }}>{participant.name} joined the room at {participant.time}</span>
                        </div>
                  </button>
                );
              })}
            </div>
          </Modal.Header> 
        </Modal>




          </div>
        </div>

    );
  }


}

const mapStateToProps = (state) => {
  return {
    auth: state.firebase.auth
  }
}

export default 
  connect(mapStateToProps, {removeStreaming, joinSession})
(StreamingCard);
