const mapStateToProps = (state) => {
  return {
      auth: state.firebase.auth,
      profile: state.firebase.profile,
      users: state.firestore.ordered.users
  }
}

export default compose(
  connect(mapStateToProps, { uploadImage }),
  firestoreConnect([
    { collection: 'users' }
  ])
)(withRouter(Profile));
