import React, { useState } from 'react';
import '../../styles/card.css';
import '../component.css';
import ditto from '../ditto.png';
import InterestCard from './interestcard.js';
import { Button, Modal } from 'react-bootstrap';
import { startConversation } from '../../redux/actions';
//Redux
import { connect } from 'react-redux';

const Card = (props) => {


  const [ show, setModal ] = useState(false);
  const [ personalities, showPersonalities ] = useState(false);
  const [ tasks, showTasks ] = useState(false);
  const [ chat, setChat ] = useState(false);
  const [ routeGuarding, setRouteGuarding ] = useState(false);


  const handleSubmit = async (e) => {
    e.preventDefault();
    const date = new Date();
    const time = date.getFullYear() + "-" + (date.getMonth() + 1) + "-" + date.getDate() + " " + date.getHours() + ":" + date.getMinutes();
    const message = createMessage(time);
    props.startConversation(props.id, props.info, props.profile, message);
    setChat(false);
  }

  const getTime = () => {
    const date = new Date();
    return date.getFullYear() + "-" + date.getMonth() + "-" + date.getDate() + " " + date.getHours() + ":" + date.getMinutes();
  }

  const createMessage = (time) => {
    let message = {};
    message = { time: time, from : props.auth.uid, content: document.getElementById("message").value };
    console.log(message);
    return message;
  }



  return (
    <div className="card-container">
      <div className='profile-content'>

      <div className="form-inline">
      <img id="props.id" src={props.info.image ? props.info.image : ditto} tabIndex="0"></img>




          <div className="info-name" style={{ marginLeft: "5px" }}>
            {props.info.first_name} {props.info.last_name}
          </div>

          {props.info.gender === "Male" ? <div style={{ backgroundColor: "skyblue", color: "white", width: "20px", textAlign: "center", borderRadius: "10%", marginLeft: "5px" }}>‚ôÇ</div> : null}
          {props.info.gender === "Female" ? <div style={{ backgroundColor: "pink", color: "white", width: "20px", textAlign: "center", borderRadius: "10%", marginLeft: "5px" }}>‚ôÄ</div> : null}
          {props.info.gender === "Other" ? <div style={{ backgroundImage: "linear-gradient(to right, skyblue, pink)", color: "white", width: "20px", textAlign: "center", borderRadius: "10%", marginLeft: "5px" }}>‚ö•</div> : null}


          <div className="info" style={{ marginLeft: "30px" }}>
        üíª Occupation: {props.info.role} {props.info.institution ? "at" : null} {props.info.institution}
        </div>
        </div>


        <div className="info">
        üí© Self-description: {props.info.self_description}
        </div>

        <div className="form-inline">
        <Button style={{ width: "25%" }}variant="success" onClick={() => setModal(true)}>üíõ Interests</Button>
              <Modal show={show} onHide={() => setModal(false)}>
                  <InterestCard info={props.info.areas_of_interest}></InterestCard>
              </Modal>

        <Button style={{ width: "30%", marginLeft: "5px" }}variant="success" onClick={() => showPersonalities(true)}>üëª Values</Button>
              <Modal show={personalities} onHide={() => showPersonalities(false)}>
                  <InterestCard info={props.info.personalities}></InterestCard>
              </Modal>


        </div>


        <div className="info">
        üìù Task description: {props.info.tasks_description}
        </div>

        <div className="info">
        <div className="form-inline">
          {props.info.tasks_tags && props.info.tasks_tags.map((p) => {
            return(
              <div className="unselected-button" style={{ fontSize: "small" }}>
                {p}
              </div>
            );
          })}
        </div>
        </div>


        {props.id !== props.auth.uid ? <Button onClick={() => {props.auth.uid ? setChat(true) : setRouteGuarding(true)}}>Start conversation</Button> : null}
        <Modal show={chat} onHide={() => setChat(false)}>
          <form onSubmit={handleSubmit}>
            <textarea id="message" required></textarea>
            <Button variant="success" type="submit">Send</Button>
          </form>
        </Modal>

        <Modal show={routeGuarding} onHide={() => setRouteGuarding(false)}>
              <Modal.Header closeButton></Modal.Header>
              <Modal.Body>
              Sign up to create a profile so that other users can know more about you!üòä
              </Modal.Body>
        </Modal>

      </div>
    </div>
  );
}


const mapStateToProps = (state) => {
  return {
    auth: state.firebase.auth,
    profile: state.firebase.profile
  }
}

export default connect ( mapStateToProps, {startConversation} )(Card);


