import React, {  Component } from "react";
import './component.css';
import ditto from './ditto.png';
import InterestCard from './design/interestcard.js';
import firebase from '../config/firebaseconfig';

class ProfileCard extends Component {
    constructor(props) {
        super(props);
        this.state = {
            profile: []
        }
    }

    componentDidMount() {
      const mounted = true;
      if (mounted === true && this.props.id !== "") {
        firebase.firestore().collection("users").doc(this.props.id).get()
        .then((doc) => {
            this.setState({
                profile: doc.data()
            })
        })
      }
    }

    render() {
        return (
            <div className='profile-content'>


        <div style={{ display: "flex" }}>
            <div className="smaller-info">
              {this.state.profile.first_name} {this.state.profile.last_name}
            </div>

            {this.state.profile.gender === "Male" ? <div style={{ backgroundColor: "skyblue", color: "white", width: "12px", textAlign: "center", borderRadius: "10%", marginLeft: "4px", marginRight: "6px" }}>♂</div> : null}
          {this.state.profile.gender === "Female" ? <div style={{ backgroundColor: "pink", color: "white", width: "12px", textAlign: "center", borderRadius: "10%", marginLeft: "4px", marginRight: "6px" }}>♀</div> : null}
          {this.state.profile.gender === "Other" ? <div style={{ backgroundImage: "linear-gradient(to right, skyblue, pink)", color: "white", width: "12px", textAlign: "center", borderRadius: "10%", marginLeft: "4px", marginRight: "6px" }}>⚥</div> : null}


                <div className="smaller-info">
              💻 Occupation: {this.state.profile.role} {this.state.profile.institution ? "at" : null} {this.state.profile.institution}
              </div>
            </div>


              <div className="smaller-info">
              💩 Self-description: {this.state.profile.self_description}
              </div>


              <div className="info" style={{ marginTop: "10px" }}>
        <div className="form-inline">
        <div className="smaller-info">
        💛 Interests: 
              </div>
          {this.state.profile.areas_of_interest && this.state.profile.areas_of_interest.map((p) => {
            return(
              <div className="unselected-button" style={{ fontSize: "small", padding: "1px", margin: "0" }}>
                {p}
              </div>
            );
          })}
        </div>
        </div>


        <div className="info">
        <div className="form-inline">
        <div className="smaller-info">
        👻 Personalities:
              </div>
          {this.state.profile.personalities && this.state.profile.personalities.map((p) => {
            return(
              <div className="unselected-button" style={{ fontSize: "small", padding: "1px", margin: "0" }}>
                {p}
              </div>
            );
          })}
        </div>
        </div>



              <div className="smaller-info">
              📝 Task description: {this.state.profile.tasks_description}
              <div className="info" style={{ marginTop: "10px" }}>
        <div className="form-inline">

          {this.state.profile.tasks_tags && this.state.profile.tasks_tags.map((p) => {
            return(
              <div className="unselected-button" style={{ fontSize: "small", padding: "1px", margin: "0" }}>
                {p}
              </div>
            );
          })}
        </div>
        </div>
              </div>

              </div>


        );
    }
}


/*
            <div className="form-inline">
                <img id="props.id" src={this.state.profile.image ? this.state.profile.image : ditto} tabIndex="0" style={{ width: "25px", height: "25px" }}></img>



                <div class="info-name" style={{ fontSize: "small" }}>
                  {this.state.profile.first_name} {this.state.profile.last_name}
                </div>
*/

export default ProfileCard;
