import React, { Component } from 'react';
import Login from './login';
import { Redirect, withRouter } from 'react-router-dom';
import './component.css';
import { connect } from 'react-redux';
import '../styles/home.css';
import { Button, Card, CardDeck, Image } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import meme from './meme.jpg';
import opening from './opening.mp4';
import {Helmet} from "react-helmet";


class Home extends Component {

  constructor(props) {
    super(props);
    this.handleStreamingClick = this.handleStreamingClick.bind(this);
    this.handleWaitingClick = this.handleWaitingClick.bind(this);

  }
/*
  componentDidMount() {
    document.getElementById("opening").play();
  }
  */

  /*
  play = () => {
    console.log("called");
    setTimeout(() => {
      document.getElementById("opening").play();
    }, 1000)
  }
  */


  handleStreamingClick = (e) => {
    e.preventDefault();
    this.props.history.push('/livestreaming');
  }

  handleWaitingClick = (e) => {
    e.preventDefault();
    this.props.history.push('/buddies');
  }

  render() {
    return (
      <div>


        <Helmet>
          <meta name="description" content="Have Some Company While You're Becoming Your Better Self"></meta>
          <title>Salon</title>
        </Helmet>

        <link
          href='https://fonts.googleapis.com/css2?family=Roboto+Slab:wght@300;400;500&display=swap'
          rel='stylesheet'
        />
        <div className='title'>
          <h1 className='title_font'>Salon</h1>
        </div>

        <div className="descript">
          <h1 className='descript_font'>
            Have some company while you're becoming your better self
          </h1>
        </div>
        <div style={{ display: "flex", marginLeft: "39%", marginTop: "2%" }}>
                <Button variant='success' onClick={this.handleStreamingClick} style={{marginRight: "50px", fontSize: "large" }}>Live Streaming</Button>
                <Button variant='success' onClick={this.handleWaitingClick} style={{ fontSize: "large" }}>Buddies</Button>
        </div>
        <video loop controls id="opening" style={{ marginTop: "3%", marginLeft: "2%", height: "400px", width: "800px" }}>
          <source src={opening} type="video/mp4"></source>
        </video>

        <div style={{ marginLeft: "20%", fontSize: "small" }}>
          <div style={{ display: "flex" }}>
          <span style={{ fontSize: "small" }}>"Synesthesia", Andi Fu, Pratt Institute painting student, 2019.</span>
          <a href="https://m.youtube.com/channel/UCPJjBUtPOH9YYraiAE2LOkw?isappinstalled=0&from=singlemessage">Youtube channel</a>
          </div>
          <div>
          <span style={{ fontSize: "small" }}>Music: Nocturne in E-flat major, Op. 9, No. 2, Frédéric Chopin, Performed by Arthur Rubinstein, Copyright ©️ 2020 Apple Inc.. Downloaded from: </span>
          <a href="https://music.apple.com/us/album/chopin-nocturnes/594180316">https://music.apple.com/us/album/chopin-nocturnes/594180316</a>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    auth: state.firebase.auth
  }
}

export default connect(mapStateToProps)(withRouter(Home));


