import React, { Component, useState } from 'react';
import { connect } from 'react-redux';
import ChatWindow from './chatwindow.js';
import { Button } from 'react-bootstrap';
import "./component.css";
import firebase from '../config/firebaseconfig';
import ProfileCard from './profilecard.js';

const ChatSection = (props) => {

    const [showProfile, setShowProfile] = useState(false);
    const [showChatWindow, setShowChatWindow] = useState(false);




        return ( 
            <button className="chatbox-section" onClick={() => setShowChatWindow(true)} /*onMouseOver={() => setShowProfile(true)} onMouseOut={() => setShowProfile(false)}*/>
                {showChatWindow ? <ChatWindow chat={props.chat} visibility={true}></ChatWindow> : null}
                {showProfile ? <ProfileCard id={props.chat.friend_id}></ProfileCard> : null}
            <div style={{ float: "left" }}>
            <img src={props.chat.friend_image} style={{ width: "40px", height: "40px", marginRight: "2px" }}></img>
            {props.chat.friend}
            </div>
            { props.chat.notifications > 0 ? <div className="notification" style={{ float: "right"}}>{props.chat.notifications}</div> : null}
            </button>
        );

}

const mapStateToProps = (state) => {
    return {
      auth: state.firebase.auth
    }
  }

  export default ChatSection;
