import React, { Component } from 'react';
import './component.css';
import firebase from '../config/firebaseconfig';
import { Redirect, withRouter } from 'react-router-dom';
import { Button, Alert } from 'react-bootstrap'
//Redux
import { connect } from 'react-redux';
import { logIn } from '../redux/actions';


class LogIn extends Component{

    state = {
        email: "",
        password: ""
    };

    handleChange = (e) => {
        e.preventDefault();
        this.setState({
            email: document.getElementById("em").value,
            password: document.getElementById("pw").value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.props.logIn(this.state, this.props.history);
    }

   render(){

    const emailStyle = {
        marginLeft: "30px"
    }

    const { auth, authError } = this.props;

    return(
        <form style={{ marginBottom: "20px"}} onSubmit={this.handleSubmit}>

                <div>
                    <label style={emailStyle}>Email:</label>
                    <input className="login_input" id="em" onChange={this.handleChange} required></input>
                </div>

                <div>
                    <label>Password:</label>
                    <input className="login_input" id="pw" onChange={this.handleChange} required></input>
                </div>

            <Button variant="success" type="submit" color="green" >Log in</Button>
            { authError ? <div className="auth_alert">{ authError }</div> : null}
        </form>
        /*
            <div style={signUpStyle}>
                <form onSubmit={this.handleSubmit}>
                    <span><br/>Email Name:</span>
                    <input id="em"placeholder="Email" onChange={this.handleChange} required></input>
                    <span><br/>Password:</span>
                    <input id="pw" placeholder="Password" onChange={this.handleChange} required></input>
                    <br/>
                    <Alert color="primary">
                        { authError ? <p>{ authError }</p> : null}
                    </Alert>
                    <button style={buttonStyle}>Enter</button>
                </form>
            </div>
            */
    )
}
}

const mapStateToProps = (state) => {
    return {
        auth: state.firebase.auth,
        authError: state.auth.authError
    }
}

export default connect(
    mapStateToProps, { logIn }
)(withRouter(LogIn));
